[H: assert( isGM(), "<b>" + getMacroName() + "</b> is a GM only macro.", 0 )]

[H: gHandouts = getLibProperty("Handouts")]

[Dialog("Edit Handout Configurations", "width=700; height=500; temporary=1"): { 
  [MACRO("buildHandoutsDialog@this"): gHandouts]
}]

[H: assert( isGM(), "<b>" + getMacroName() + "</b> is a GM only macro.", 0 )]
[H: gMaps = getLibProperty("Maps")]

[Dialog("Edit Maps & Destinations", "width=600; height=400; temporary=1"): { 
  [MACRO("buildMapsLocsDialog@this"): gMaps]
}]

[H: action = json.get(macro.args, "editMaps")]
[H: tRM = json.get(macro.args, "returnMacro")]
[H: libProp = json.get(macro.args, "property")]
[H: template = json.get(getLibProperty("MapsTemplate"), 0)]
[H: tjProp = getLibProperty(libProp)]

[H: maps = getAllMapNames("json")]
[H: numMaps = json.length(maps)]
[H: mapsAdded = '[]']
[H, FOREACH(map, tjProp): mapsAdded = json.append(mapsAdded, json.get(map, "mapName"))]
[H, FOREACH(map, maps), CODE: {
  [R, IF(json.contains(mapsAdded, map)): maps = json.remove(maps, json.indexOf(maps, map))]
}]

[H: index = listGet(action, 2, " ")]

[H, IF(action == "Save"), CODE: {
  [H: targs = json.set(macro.args, "controlKeys", '["editMaps","property","returnMacro","controlKeys"]')]
  [MACRO("saveJSONDialogChanges@" + getMacroLocation()): targs]
  [H: output = "Changes saved."]
}]

[H, IF(action == "Add Map"), CODE: {
  [H: canAdd = if((json.length(maps) > 0) && (numMaps > json.length(mapsAdded)), 1, 0)]
  [H, IF(canAdd), CODE: {
    [H: ok = input(
       "selMap | " + json.toList(maps) + "| Select a map | LIST | VALUE=STRING"
    )]
    [H: abort(ok)]

    [H: setLibProperty( libProp, json.append(tjProp, json.set(template, "mapName", selMap)) )]
    [H: output = "Map added."]
  };{
    [H: output = "All campaign maps already available, so no map was added."]
  }]
}]

[H, IF(startsWith(action,"Remove Map")), CODE: {
  [H: setLibProperty(libProp, json.remove(tjProp, index))]
  [H: output = "Map removed."]
}]

[H, IF(startsWith(action,"Add EP")), CODE: {
  [H: subtemplate = json.get(json.get(template, "entryPoints"), 0)]
  [H: tjM = json.get(tjProp, index)]
  [H: tjEP = json.get(tjM, "entryPoints")]
  [H: tjOut = json.set( tjProp, index, json.set( tjM, "entryPoints", json.append(tjEP, subtemplate)))]
  [H: setLibProperty(json.get(macro.args, "property"), tjOut)]
  [H: output = "Entry point added."]
}]

[H, IF(startsWith(action,"Remove EP")), CODE: {
  [H: subindex = listGet(action, 3, " ")]
  [H: tjM = json.get(tjProp, index)]
  [H: tjSA = json.remove(json.get(tjM, "entryPoints"), subindex)]
  [H: tjFP = json.set(tjProp, index, json.set(tjM, "entryPoints", tjSA))]
  [H: setLibProperty(libProp, tjFP)]
  [H: output = "Entry point removed."]
}]

[R: output]

[H, IF(tRM != ""), CODE: { [MACRO(tRM+"@"+getMacroLocation()): ""] }]

&nbsp;
<!-- "
property = Campaign Property name
json = the full json array sent from the Dialog Form Submit.  The non-property keys must be listed in array form in the property "controlKeys"
" -->

[H: controlKeys =  json.get(macro.args, "controlKeys")]
[H: tjProp = getLibProperty( json.get(macro.args, "property") )]
[H: tRM = json.get(macro.args, "returnMacro")]
[H: tJF = json.fields(macro.args, 'json')]
[H, FOREACH(cntrl, controlKeys), CODE: {
  [R, IF(json.contains(tJF, cntrl)): tJF = json.remove(tJF, json.indexOf(tJF, cntrl))]
}]

[H, FOREACH(fn, tJF, ""), CODE: {
  [H: tV = json.get(macro.args, fn)]
  [R: tjProp = json.pset(tjProp, fn, tV)]
}]

[H: setLibProperty(json.get(macro.args, "property"), tjProp)]

&nbsp;

[H: bgImage = macro.args]

[ok = input(
   "variableName| " + bgImage + "|<html><head><style type='text/css'> table{ float:center; border-width: 1px; border-spacing: 1px; border-collapse: collapse; }</style></head><body>Press <table><tr><td>Cancel</tr></td></table> if you see a red ? image - - ></body></html> |LABEL|ICON=TRUE",
   "junk| (Press Ok if the image is rendered correctly.) | | LABEL | SPAN=TRUE",
   "junk| Note that this is a workaround for an image loading bug. | | LABEL | SPAN=TRUE"
)]

[H, IF(!ok), CODE: {
   [MACRO("LibHandoutPreload@this"): bgImage]
}]

[H: element = getStrProp(macro.args, "Element")]
[H, SWITCH(element):
case 0: image = getTokenImage();
case 1: image = getTokenPortrait();
case 2: image = getTokenHandout();
default: image = ""
]
[H, SWITCH(element):
case 0: title = token.name;
case 1: title = token.gm_name;
case 2: title = token.label;
default: title = token.name
]

[H: text = getStrProp(macro.args, "Text")]
[H: FreeText = getStrProp(macro.args, "FreeText")]
[H, SWITCH(text):
case 0: text = getNotes();
case 1: text = getGMNotes();
case 2: text = FreeText;
default: text = ""
]

[H: title = if(title == "", token.name, title)]
[H: title = "Handout: " + title]

[H: output = if(image == "", text, "<img src='" + image + "'></image><br />" + text)]

[Frame(title): {
  [R: output]
}]
[H: abort(0)]

[h: toSend = '{}']
[h: argTest = json.type(arg(0))]
[h, if(argTest=='ARRAY'), code:
{
    [h: toWho = 'list']
    [h: toSend = json.set(toSend, 'mlOutputList', arg(0))]
};{
    [h: toWho = arg(0)]
}]
[h: toSend = json.set(toSend, 'toSend', arg(1))]
[h: conditionalOutput = macroLinkText('conditionalOutput@this', toWho, toSend)]
[h: execLink(conditionalOutput, 1)]
